import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../COI.css';

export default function PartsIndex() {
  const [parts, setParts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://localhost:5000/api/parts') // make sure your frontend is hitting the right URL
      .then(res => res.json())
      .then(setParts)
      .catch(error => console.error('Error fetching parts:', error));
  }, []);

  return (
    <div className="coi-container">
      <h2>Constitution of India â€“ Parts Index</h2>
      <div className="part-list">
        {parts.map((part) => (
          <div
            key={part.partNumber}
            className="part-card"
            onClick={() => navigate(`/parts/${part.partNumber}`)}
          >
            <div className="part-title">{part.title}</div>
            <div className="part-subtext">Click to view articles</div>
          </div>
        ))}
      </div>
    </div>
    
  );
}
