import React, { useEffect, useState } from 'react';
import '../Quiz.css';
// import axios from 'axios';

export default function QuizPage() {
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState({});
  const [score, setScore] = useState(null);
  const [topScorers, setTopScorers] = useState([]);

  const email = localStorage.getItem('email'); // check login

  useEffect(() => {
    if (!email) return;

    axios.post('http://localhost:5000/generate-quiz')
      .then(res => setQuestions(res.data))
      .catch(() => alert('Error loading quiz'));
  }, []);

  const handleChange = (qIdx, selected) => {
    setAnswers({ ...answers, [qIdx]: selected });
  };

  const handleSubmit = async () => {
    let score = 0;
    questions.forEach((q, i) => {
      if (answers[i] === q.answer) score++;
    });

    setScore(score);

    await axios.post('http://localhost:5000/submit-quiz', { score, email });
    const res = await axios.get('http://localhost:5000/top-scorers');
    setTopScorers(res.data);
  };

  if (!email) {
    return <div className="quiz-login-msg">üîê Please login to attempt the quiz.</div>;
  }

  return (
    <div className="quiz-container">
      <h2>üß† Constitution Quiz Challenge</h2>
      <p>Test your knowledge with AI-generated MCQs. Best of luck!</p>

      {questions.map((q, idx) => (
        <div key={idx} className="quiz-question">
          <h4>{idx + 1}. {q.question}</h4>
          {q.options.map((opt, i) => (
            <label key={i}>
              <input
                type="radio"
                name={`q-${idx}`}
                value={opt}
                onChange={() => handleChange(idx, opt)}
              />
              {opt}
            </label>
          ))}
        </div>
      ))}

      {questions.length > 0 && (
        <button className="quiz-submit-btn" onClick={handleSubmit}>Submit Quiz</button>
      )}

      {score !== null && (
        <div className="quiz-result">
          <h3>Your Score: {score}/10</h3>
          <p>{score >= 8 ? "üèÜ Excellent!" : score >= 5 ? "üëç Good effort!" : "üìò Keep learning!"}</p>
        </div>
      )}

      {topScorers.length > 0 && (
        <div className="quiz-leaderboard">
          <h3>üèÖ Top 5 Scorers</h3>
          <ol>
            {topScorers.map((user, i) => (
              <li key={i}>{user.username} ‚Äì {user.highscore}</li>
            ))}
          </ol>
        </div>
      )}
    </div>
  );
}
