import React from 'react';
import Navbar from './Navbar';
import preambleImage from '../Images/preamble.png';
import '../App.css';

function Preamble() {
  return (
    <>
      <Navbar />
      <div className="preamble-container">
        <img src={preambleImage} alt="Preamble" className="preamble-img" />
        <div className="preamble-text">
          <h2>Understanding the Preamble</h2>
          <p>
            The Preamble to the Constitution of India is a brief introductory statement that sets out the guiding purpose, principles, and philosophy of the document. It reflects the hopes and aspirations of the people of India.
          </p>
          <h3>Key Terms Explained:</h3>
          <ul>
            <li><a href="https://en.wikipedia.org/wiki/Sovereign_state" target="_blank" rel="noopener noreferrer"><strong>Sovereign</strong></a>: Independent authority of India to govern itself.</li>
            <li><a href="https://en.wikipedia.org/wiki/Socialism" target="_blank" rel="noopener noreferrer"><strong>Socialist</strong></a>: Social and economic equality among all citizens.</li>
            <li><a href="https://en.wikipedia.org/wiki/Secularism_in_India" target="_blank" rel="noopener noreferrer"><strong>Secular</strong></a>: Equal treatment of all religions by the state.</li>
            <li><a href="https://en.wikipedia.org/wiki/Democracy" target="_blank" rel="noopener noreferrer"><strong>Democratic</strong></a>: Government elected by the people.</li>
            <li><a href="https://en.wikipedia.org/wiki/Republic" target="_blank" rel="noopener noreferrer"><strong>Republic</strong></a>: Head of state is elected, not a monarch.</li>
            <li><a href="https://en.wikipedia.org/wiki/Justice" target="_blank" rel="noopener noreferrer"><strong>Justice</strong></a>: Social, economic, and political fairness.</li>
            <li><a href="https://en.wikipedia.org/wiki/Liberty" target="_blank" rel="noopener noreferrer"><strong>Liberty</strong></a>: Freedom of thought, expression, belief, faith, and worship.</li>
            <li><a href="https://en.wikipedia.org/wiki/Equality_before_the_law" target="_blank" rel="noopener noreferrer"><strong>Equality</strong></a>: Equal status and opportunities for all citizens.</li>
            <li><a href="https://en.wikipedia.org/wiki/Fraternity" target="_blank" rel="noopener noreferrer"><strong>Fraternity</strong></a>: Brotherhood and unity among all Indians.</li>
          </ul>
        </div>
      </div>
    </>
  );
}

export default Preamble;
