// src/components/PartsIndex.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../COI.css';

export default function PartsIndex() {
  const [parts, setParts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('/api/parts')
      .then(res => res.json())
      .then(setParts);
  }, []);

  return (
    <div className="coi-container">
      <h2>Constitution of India – Parts Index</h2>
      <div className="part-list">
        {parts.map(p => (
          <div
            key={p.partNumber}
            className="part-card"
            onClick={() => navigate(`/parts/${p.partNumber}`)}
          >
            <div className="part-title">{p.name}</div>
            <div className="part-range">
              Articles {p.startArticle}–{p.endArticle}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
