import React, { useState, useEffect } from 'react';
import '../Quiz.css';

function Quiz() {
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState({});
  const [submitted, setSubmitted] = useState(false);
  const [scoreInfo, setScoreInfo] = useState(null);
  const [leaderboard, setLeaderboard] = useState([]);

  useEffect(() => {
    fetch('/quiz/generate')
      .then(res => res.json())
      .then(data => setQuestions(data.questions));
    fetch('/quiz/leaderboard')
      .then(res => res.json())
      .then(data => setLeaderboard(data.top));
  }, []);

  const handleChange = (idx, opt) => {
    setAnswers(a => ({ ...a, [idx]: opt }));
  }

  const handleSubmit = () => {
    fetch('/quiz/submit', {
      method: 'POST', headers: {'Content-Type':'application/json'},
      body: JSON.stringify({questions, answers})
    })
    .then(res => res.json())
    .then(data => {
      setScoreInfo(data);
      setSubmitted(true);
      // refresh leaderboard
      fetch('/quiz/leaderboard')
        .then(res => res.json())
        .then(data => setLeaderboard(data.top));
    });
  }

  if (!questions.length) return <div>Loading quiz...</div>;

  return (
    <div className="quiz-container">
      <h2>Constitution Quiz</h2>
      {!submitted ? (
        <>
          {questions.map((q,i) => (
            <div key={i} className="quiz-question">
              <p><strong>{i+1}. {q.question}</strong></p>
              {q.options.map((opt,j) => (
                <label key={j}>
                  <input
                    type="radio"
                    name={'q'+i}
                    value={j}
                    checked={answers[i] == j}
                    onChange={()=>handleChange(i,j)}
                  />
                  {opt}
                </label>
              ))}
            </div>
          ))}
          <button onClick={handleSubmit}>Submit Quiz</button>
        </>
      ) : (
        <div className="quiz-result">
          <p>Your Score: {scoreInfo.score}/10</p>
          <p>{scoreInfo.remark}</p>
        </div>
      )}

      <div className="leaderboard">
        <h3>Top 5 Scorers</h3>
        <ol>
          {leaderboard.map((r,i)=><li key={i}>{r.username}: {r.highscore}</li>)}
        </ol>
      </div>
    </div>
  );
}

export default Quiz;
