import React from 'react';
import './Rights.css';
import { useNavigate } from 'react-router-dom';

export default function FundamentalRightsPage() {
  const navigate = useNavigate();

  const rights = [
    { name: "Right to Equality", summary: "Equality before law, no discrimination.", path: "/right-i" },
    { name: "Right to Freedom", summary: "Freedom of speech, expression, movement, etc.", path: "/right-ii" },
    { name: "Right Against Exploitation", summary: "Abolishes trafficking and child labor.", path: "/right-iii" },
    { name: "Right to Freedom of Religion", summary: "Practice and propagate any religion.", path: "/right-iv" },
    { name: "Cultural and Educational Rights", summary: "Preserve culture and access education.", path: "/right-v" },
    { name: "Right to Constitutional Remedies", summary: "Approach courts if rights are violated.", path: "/right-vi" }
  ];

  return (
    <div className="rights-cards-container">
      <h2>Fundamental Rights of the Indian Constitution</h2>
      <div className="rights-cards-grid">
        {rights.map((right, index) => (
          <div className="rights-card" key={index} onClick={() => navigate(right.path)}>
            <h3>{right.name}</h3>
            <p>{right.summary}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
