// src/components/Preamble.js
import React from 'react';
import Navbar from './Navbar';
import '../App.css';
import preambleImg from '../Images/Preamble.jpg'; // Your image file

const Preamble = () => {
  return (
    <>
      <Navbar />
      <div className="hero-section">
        <img src={preambleImg} alt="Preamble of India" className="preamble-img" />
      </div>

      <div className="preamble-content">
        <h2>Understanding the Preamble</h2>
        <p>
          The Preamble of the Indian Constitution is a brief introductory statement that sets out the guiding purpose, principles, and philosophy of the Constitution. It reflects the hopes and aspirations of the people.
        </p>
        <h3>Key Words Explained:</h3>
        <ul>
          <li><a href="https://en.wikipedia.org/wiki/Sovereign_state" target="_blank" rel="noreferrer">Sovereign</a> – India's authority is independent of any external power.</li>
          <li><a href="https://en.wikipedia.org/wiki/Socialism" target="_blank" rel="noreferrer">Socialist</a> – A commitment to social equity and wealth distribution.</li>
          <li><a href="https://en.wikipedia.org/wiki/Secular_state" target="_blank" rel="noreferrer">Secular</a> – No official state religion; freedom of faith for all citizens.</li>
          <li><a href="https://en.wikipedia.org/wiki/Democracy" target="_blank" rel="noreferrer">Democratic</a> – Government by the people through free and fair elections.</li>
          <li><a href="https://en.wikipedia.org/wiki/Republic" target="_blank" rel="noreferrer">Republic</a> – The head of state is elected, not a monarch.</li>
          <li><a href="https://en.wikipedia.org/wiki/Justice" target="_blank" rel="noreferrer">Justice</a> – Social, economic, and political justice for all.</li>
          <li><a href="https://en.wikipedia.org/wiki/Equality_before_the_law" target="_blank" rel="noreferrer">Equality</a> – Equal rights and opportunities for all.</li>
          <li><a href="https://en.wikipedia.org/wiki/Liberty" target="_blank" rel="noreferrer">Liberty</a> – Freedom of thought, expression, belief, faith, and worship.</li>
          <li><a href="https://en.wikipedia.org/wiki/Fraternity" target="_blank" rel="noreferrer">Fraternity</a> – Brotherhood and unity among citizens.</li>
        </ul>
      </div>
    </>
  );
};

export default Preamble;
